import subprocess

class BaseService(object):
    '''This is the base class for services.
    '''
    def __init__(self, appliance, config):
        self.appliance = appliance
        self.config = config

    def start(self):
        return False

    def stop(self):
        return False

    def restart(self):
        return False

    def stat(self):
        return False

    def pid(self):
        return -1
