#!/usr/bin/env python
#encoding: utf-8

'''This is the ridiculously simple Page class which represents a web page. 
'''

class Page(object):
    def __init__(self, title='', css='', contentType='text/html', charSet='utf-8'):
        self.title = title
        self.css = css
        self.contentType = contentType
        self.charSet = charSet
        self.head = ''
        self.body = ''

    def reset(self):
        self.__init__()

    def setCSS(self, path=''):
        self.css = path

    def getCSS(self):
        return self.css

    def setCharSet(self, charSet):
        self.charSet = charSet

    def getCharSet(self):
        return self.charSet

    def setContentType(self, type):
        self.contentType = type

    def getContentType(self):
        return self.contentType

    def setTitle(self, text):
        self.title = text

    def getTitle(self):
        return self.title

    def setHead(self, head):
        self.head += head

    def getHead(self):
        return self.head

    def delHead(self):
        self.head = ''

    def setBody(self, body):
        self.body += body

    def getBody(self):
        return self.body

    def delBody(self):
        self.body = ''

    def output(self):
        'Output the whole page!'
        output = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">'
        output += '<html>'
        output += '<head>'
        output += '<meta http-equiv="content-type" content="%s; charset=%s" />' % (self.contentType, self.charSet)
        if self.head:
            output += self.head
        if self.css:
            output += '<link rel=StyleSheet href="%s" type="text/css" />' % self.css
        output += '<title>%s</title>' % self.title
        output += '</head>'
        output += '<body>'
        output += self.body
        output += '</body>'
        output += '</html>'
        return output
